<?phpclass loginController {	private $model;	private $view;			public function __construct() {		$this -> model = new loginModel();		$this -> view = new loginView();	}		public function indexAction() {		return $this -> view -> login();	}		public function loginAction() {		$pwd =(isset($_POST['pwd'])?$_POST['pwd']:'');		$response = $this -> model -> login($pwd);		return $response;			}}